<?php//============================================================//Filename : index.php//Variables ://  $_POST://      'submit' - has submit button been pressed.//      'User' - Username submitted.//      'Pass' - Password submitted.//  $_GET://      'logout' - if set to 1 then logout account.//  $_SESSION://      'usertype' - The type of user currently logged in.//      'nologin' - login has failed for some reason.//      'name' - Name of logged in user.//      'email' - Email of logged in user.//      'buid' - BUID of logged in user.////============================================================//Comments End Here===========================================	if ( !isset($_SESSION['usertype']) )	{    	session_start();    }?><! DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN"><html>    <head>        <meta http-equiv="Content-Type" content="text/html; charset=UTF-8">        <link rel="stylesheet" type="text/css" href="Style.css" />        <title></title>    </head>    <body bgcolor="#EEEEEE">        <?php        	include 'dbvars.php';            /*if ( isset($_GET['logout']) )            {            	print("<p>_GET['logout'] is set to ".$_GET['logout']);            }            else            {            	print("<p>_GET['logout'] is not set");            }*/    		//print_r($_SESSION);            //The user attempted to log out.            if (isset($_GET['logout']) && $_GET['logout'] == 1) {            	// print("index.php@5: destroying the session");                //Destroy the session.                $_SESSION['usertype'] = 0;                session_destroy();            }            //print("index.php@7 <br>");            //The user attempted to log in.            //if (isset($_POST['submit']))            //{            //	print("The user attempted to log in.<br>");            //}            //The user attempted to login.            if (isset($_POST['submit']))            {                //If username or password boxes were empty then disallow login.                if (!$_POST['User'] | !$_POST['Pass'])                {                    //Set login error.                    $_SESSION['nologin'] = 1;                }                else                {                   //Create a new connection to the MySQL database.                   // print("index.php@0.5; about to connect to database server<br>");                   mysql_connect($serverurl, $adminname, $adminp)                    	or die(mysql_error("could not connect to database server"));                   // print("index.php@0.6; connected to database server; about to select database<br>");                   //Select database.                    mysql_select_db($dbname) or die(mysql_error("could not select database"));                    // print("index.php@1; apparently can access database<br>");                    //Store username and password in local variables.                    $email = $_POST['User'];                    $pass = md5($_POST['Pass']);                    //Return all rows with the same username, should be only one row.                    $check = mysql_query("SELECT * FROM nLogin WHERE email = '". $_POST['User']. "'")or die(mysql_error());                    $info = mysql_fetch_array( $check );                    // print("index.php@2; apparently read nLogin record<br>");                    //Check if the password matches and if the account has been activated(email activation flag).                    if ($pass == $info['password'] && $info['isactive'] >= 1)                    {                    	//print("index.php@3; apparently authentication was successful.<br>");                        //Authentication successful. Store/Set session/global variables for future use.                        $_SESSION['name'] = $info['name'];                        $_SESSION['email'] = $info['email'];                        $_SESSION['buid'] = $info['buid'];                        $_SESSION['usertype'] = $info['isactive'];                        $_SESSION['nologin'] = 0;                        // print("index.php@3.9; set session vars: name: [".$_SESSION['name']."]");                    }                    else                    {                     	//print("index.php@3; apparently authentication failed.<br>");                       //Authentication failed. Set login error flag.                        $_SESSION['nologin'] = 1;                    }                }            }            //Include website header.			$thisfilename = "index.php";            include 'header.php';			$not_logged_in = !isset($_SESSION['usertype'])				|| (isset($_SESSION['usertype']) && $_SESSION['usertype'] == 0);            if ($not_logged_in)            {                //If the user is not logged in the include the login box.                include 'login.php';            }            else            {                //If the user is logged in then include the website management page.                include 'manage.php';            }            //Include the website footer.            include 'footer.php';        ?>    </body></html>